import * as Actions from "./actions";
import initialState from "../store/initialState";

export const MoviesReducer = (state = initialState.movies, action) => {
    switch (action.type) {
        case Actions.FETCH_MOVIE:
            return {
                ...state,
                results: action.payload.movies.results
            };
        default:
            return state;
    }
};
